    <script>
        function toggleDropdown(menuId, arrowId) {
            const dropdown = document.getElementById(menuId);
            const arrow = document.getElementById(arrowId);
            const card = document.getElementById('dropdown-card1');
            const svgIcon = card.querySelector('svg');

            const isOpen = !dropdown.classList.contains('hidden');

            if (isOpen) {
                dropdown.classList.add('hidden');
                arrow.classList.remove('rotate-180');
                card.classList.remove('bg-white', 'text-primary');
                card.classList.add('text-white');
                svgIcon?.classList.remove('text-primary');
                svgIcon?.classList.add('text-white');
            } else {
                openDropdown(menuId, arrowId, 'dropdown-card1');
            }
        }

        function openDropdown(menuId, arrowId, cardId) {
            const dropdown = document.getElementById(menuId);
            const arrow = document.getElementById(arrowId);
            const card = document.getElementById(cardId);
            const svgIcon = card.querySelector('svg');

            dropdown.classList.remove('hidden');
            arrow.classList.add('rotate-180');
            card.classList.add('bg-white', 'text-primary');
            card.classList.remove('text-white');
            svgIcon?.classList.remove('text-white');
            svgIcon?.classList.add('text-primary');
        }
    </script>
    <script>
        document.addEventListener('DOMContentLoaded', () => {
            fetch("{{ route('dashboard.counts') }}")
                .then(res => res.json())
                .then(data => {
                    updateBadge('orderCount', data.orders);
                    updateBadge('approveProductCount', data.approved);
                    updateBadge('queriesCount', data.queries);
                    updateBadge('creditCount', data.credit);
                    updateBadge('sellerCount', data.seller);
                    updateBadge('freelancerCount', data.freelancer);
                    updateBadge('sumSF', data.sumSF);
                });
        });

        function updateBadge(id, count) {
            let displayCount = count > 99 ? '99+' : count;
            let el = document.getElementById(id);
            if (el) {
                if (count > 0) {
                    el.textContent = displayCount;
                    el.style.display = 'flex';
                } else {
                    el.textContent = '';
                    el.style.display = 'none';
                }
            }
        }
    </script>



